INSERT INTO "public"."processed_reviews" VALUES ('584ec4aa-12d3-4420-884c-cdbd876f1d81', 'banki.ru', 'Хочу выразить огромную благодарность представителю банка(Александру) за отличное обслуживание .У меня не получалось забрать карту в назначенном месте,т.к изменились обстоятельства,Александр сам проявил инициативу и привез карту в другой конец города,после рабочего времени. Сотрудник банка на встрече оформил мне карту.', 'neutral', 'общее', 'М', 'Москва', 'Москва', 'RU-MOW', '2024-03-04 14:06:00+07', '2025-09-30 12:17:40.043787+07');
