INSERT INTO "public"."processed_reviews" VALUES ('476cb997-9764-46b1-a951-2eb015e325b2', 'banki.ru', 'Очень быстро ответил специалист 24 января 2024 года в 19:00 и пояснил,что есть такая возможность и необходимо подать заявление в онлайн чате или офисе банка ,за эту услугу берется комиссия,так же специалист скинул ссылку для более подробной информации. Решением моего вопроса Я осталась довольна, выражаю благодарность специалисту. Дополнение к отзыву отправлено Вам на электронную почту.', 'negative', 'общее', 'М', 'Смоленск', 'Смоленская область', 'RU-SMO', '2024-01-24 19:20:00+07', '2025-09-30 12:17:40.283265+07');
