INSERT INTO "public"."processed_reviews" VALUES ('7e231df0-7db1-4908-8df3-742f5e5b0c0c', 'banki.ru', 'Наступает новый год и всё хотят подарки, я решил отправить денежные средства своей подруге но слышал что по переводу спб может быть комиссия я решил обратиться к специалисту в онлайн чат 20.12.2023 года в 11:38 сначала ответит бот, потом пришлось не много подождать и подключился Специалист Степан, который чётко и по существу рассказал о переводе. Спасибо ему решил мою проблему! Дополнение к отзыву отправлено Вам на электронную почту', 'negative', 'общее', NULL, 'Москва', 'Москва', 'RU-MOW', '2023-12-20 12:13:00+07', '2025-09-30 12:17:40.283265+07');
