INSERT INTO "public"."processed_reviews" VALUES ('b3461fb6-6f54-4d1a-8854-ed8b20d7da0e', 'banki.ru', '16.11.2023 была подана на сайте заявка на дебетовую карту. В этот же день пришло СМС уведомление о получении карты в офисе. 21.11.2023 пошел забирать в офис карту. Выбирал именно эту карту т.к меня устроили условия карты Газпромбанка. Также получил за оформление карты кешбэк в сумме 1000 бонусов + от парнера 1000 сертификат Озон.', 'negative', 'бонусные программы', NULL, 'Дубинино', 'Красноярский край', 'RU-KYA', '2023-12-26 16:09:00+07', '2025-09-30 12:17:40.283265+07');
