INSERT INTO "public"."processed_reviews" VALUES ('8d1b9b68-3559-4e1f-b450-16c69df217b7', 'banki.ru', '17 декабря в 16:27 мне ответил специалист банка ,уточнил у меня,какая операционная система и скинул магазины,в которых можно обновить или заново закачать мобильное приложение. Решением моего вопроса Я осталась довольна, выражаю благодарность специалисту.', 'positive', 'мобильное приложение', NULL, 'Смоленск', 'Смоленская область', 'RU-SMO', '2023-12-17 19:18:00+07', '2025-09-30 12:17:40.283265+07');
