INSERT INTO "public"."processed_reviews" VALUES ('f9a02aca-97f1-4303-9283-5e68e1aad02b', 'banki.ru', 'Заказала карту Газпромбанк с доставкой. За день до встречи договорились на первую половину дня, однако утром , в связи с обстоятельствами сообщила курьеру Дмитрию , что смогу только встретиться вечером ближе к 17ч . Не смотря на такое обстоятельство , Дмитрий доставил карту в изменённое время . На встрече помог активировать карту , зарегистрировать в личном кабинете , ознакомил с функциями главного меню, и рассказал о лимитах и переводах, узнала то что есть накопительный счёт 15,5% на ежедневный остаток. Встречей довольна.', 'positive', 'дебетовые карты', NULL, 'Краснодар', 'Краснодарский край', 'RU-KDA', '2023-12-15 17:34:00+07', '2025-09-30 12:17:40.283265+07');
