INSERT INTO "public"."processed_reviews" VALUES ('960a2e1b-b43b-4ed1-8b56-c8565358cd84', 'banki.ru', 'Выражаю большую благодарность за работу представителя банка - М-ко Дмитрия А. Специалист лично связался за день до назначенной встречи, уточнил все детали и по моей просьбе согласился перенести адрес и время встречи в более удобное для меня месторасположение. Подробно были разъяснены условия по выбранному мной продукту , а также другие детали банка. На все интересующие вопросы были получены подробные ответы. Благодарю за ответственное отношение к работе и уважительное, вежливое отношение к клиентам!', 'positive', 'общее', NULL, 'Омск', 'Омская область', 'RU-OMS', '2023-12-13 06:06:00+07', '2025-09-30 12:17:40.283265+07');
