INSERT INTO "public"."processed_reviews" VALUES ('b9614da7-adf8-4181-8fd9-e377b45cdc64', 'banki.ru', 'Хочу поблагодарить сотрудника Газпромбанка К-ву Марианну, карта была очень нужна, по причине болезни ребенка не могла никак попасть на встречу, несколько раз переносила встречу. Специалист попалась очень отзывчивая и понимающая, привезла карту на домашний адрес. Провела встречу быстро. Карту получила. Спасибо Газпромбанку за таких прекрасных специалистов.', 'positive', 'общее', NULL, 'Краснодар', 'Краснодарский край', 'RU-KDA', '2023-12-11 15:31:00+07', '2025-09-30 12:17:40.283265+07');
