INSERT INTO "public"."processed_reviews" VALUES ('aec0da11-74de-4ee5-b87f-2eceec17b104', 'banki.ru', 'Добрый день, заказывал дебетовую карту МИР на сайте банка, была возможность получить в офисе или получить с доставкой представителем банка по удобному для меня адресу и времени. Через некоторое время поступил звонок от представителя банка, договорились об удобном времени в течение дня. Не смотря на сильный мороз в минус 30, представитель приехал ко мне в офис, без опозданий, вежливый общительный парень по имени Алексей, сфотографировал, оформили документы, активировали карту. Также рассказал подробно об этой карте и о других продуктах. Все сделали быстро, установил пин код и за покупками!', 'positive', 'дебетовые карты', NULL, 'Новосибирск', 'Новосибирская область', 'RU-NVS', '2023-12-11 13:11:00+07', '2025-09-30 12:17:40.283265+07');
