INSERT INTO "public"."processed_reviews" VALUES ('d478b978-7fff-4e5a-9801-341ebfa17b5d', 'banki.ru', 'Выдача карты, консультация. Всё прошло успешно, карта выдана своевременно, работает исправно Благодарю сотрудников банка. Спасибо. Выдача карты, консультация. Всё прошло успешно, карта выдана своевременно, работает исправно Благодарю сотрудников банка. Спасибо.', 'positive', 'общее', NULL, 'Чайковский', 'Пермский край', 'RU-PER', '2023-12-11 08:50:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('5b927244-f84f-4b8a-82cc-25944aa0ba2f', 'banki.ru', 'Муртазаев Рамиль привез карту, все вовремя. привезли по другому адресу, дальше , чем было сказано ранее, в другой район, за 20км от указанного мной адреса. Не ожидала такого сервиса от данного банка и была приятно уд ивоена, сама выдача карты прошла быстро и на позитиве большое спасибо такой приятному сотруднику!!)))', 'positive', 'общее', NULL, 'Волгоград', 'Волгоградская область', 'RU-VGG', '2023-12-06 14:29:00+07', '2025-09-30 12:17:40.283265+07');
