INSERT INTO "public"."processed_reviews" VALUES ('bf8ef783-03fe-4f6a-acac-549d6aee05a1', 'banki.ru', 'Здравствуйте, в приложении Газпромбанка оставил заявку на Дебетовую карту на 29.11.2023г. , заявку приняла специалист Екатерина, переговорили, условились что адрес передачи карты может поменятся, без проблем привезла, показала, расказала условия по карте, так же прорекламировала кредитную карту, так как есть мысли по приобретению оной, информация пришлась кстати.', 'negative', 'дебетовые карты', NULL, 'Екатеринбург', 'Свердловская область', 'RU-SVE', '2023-11-29 18:28:00+07', '2025-09-30 12:17:40.283265+07');
