INSERT INTO "public"."processed_reviews" VALUES ('ea58f0ba-964c-4ed3-a5a4-d851e0b87a60', 'banki.ru', 'Доставку оформила на следующий день. Представитель Газпромбанка М-ко Дмитрий А. позвонил с утра и согласовал время встречи. В общении был приветлив и дружелюбен. Подписали документы. Всё очень подробно рассказал о карте, проговорил все действия по активации, подключению и отключению услуг. Лимиты и тарифы на снятия и переводы. Грамотный и вежливый специалист. Рекомендую.', 'positive', 'общее', NULL, 'Омск', 'Омская область', 'RU-OMS', '2023-12-01 06:01:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('c25e6d87-dec9-4871-bff4-b315ed44f5d4', 'banki.ru', 'Заказала дебетовую карту, на встречу приехала представитель Екатерина, максимально подстроилась под мои планы, все объяснила, очень доступно предоставила информацию об условиях карты, кэшбэке, помогла пройти регистрацию в приложении, также выдала дополнительно кредитную карту, удивили и порадовали условия, буду пользоваться с удовольствием.', 'neutral', 'дебетовые карты', NULL, 'Оренбург', 'Оренбургская область', 'RU-ORE', '2023-11-29 12:38:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('862e97f3-0b71-4249-a372-f847f3eb943f', 'banki.ru', 'Сотрудник Рамиль очень вежливый, приехал во время. Все хорошо рассказал о возможностях пользования. О выгодном кешбек. У меня были заказаны в карты в разные дни должны били доставить. Сотрудник привёз в один день. За это огромное спасибо, так как в другой день не могла забрать карту. Спасибо большое. Однозначно рекомендую.', 'positive', 'общее', NULL, 'Волгоград', 'Волгоградская область', 'RU-VGG', '2023-11-27 15:23:00+07', '2025-09-30 12:17:40.283265+07');
