INSERT INTO "public"."processed_reviews" VALUES ('9ecb0c21-15e8-4c74-a558-6e4554c717b8', 'banki.ru', 'Перед встречей он позвонил,согласовал точное время доставки , а при встрече подробно и профессионально рассказал про использование карты, про мобильную связь, я даже решила перейти на Газпромбанк Мобайл. В особенности понравилось, что Юрий честно и прямо рассказал о моментах, где могут автоматически списываться деньги ( подписки, привилегии),о чем не рассказывают представители других банков. Очень довольна работой Юрия.', 'positive', 'мобильная связь', NULL, 'Омск', 'Омская область', 'RU-OMS', '2023-11-27 11:43:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('7d9a43ce-2af7-4308-8751-db95baead97e', 'banki.ru', 'Сотрудница Марии номер оператора не звала сказал что достаточно будет времени звонка позвонила я сегодня 22 ноября 2023 года в 13:41 звонок длился 20 минут проконсультировала меня по поводу оплаты и кешбэк по премиальной карте а точнее мне нужно было узнать если я потрачу 50 000 руб. то получу 1% по своей премиальной карте или же нет Мария сказала о том что есть такое по премиальной карте когда ты тратишь в месяц несколько покупок делаешь то получаешь за самую наибольшую покупку 5% соответственно я её спросила и мы с ней высчитали если я потрачу грубо говоря 100 руб. в магазине продуктовом и 50 000 потрачу на покупку тура в турагентстве тогда за данную покупку 50 000 руб. я получу 5% подумала я. Мария объяснила что это работает не так а начисляется 5% на 20% от общих трат за месяц соответственно 50 100 руб. то это будет так что 20% от этой стоимости это 10 020 руб. вот на эту сумму будет начислена 5% соответственно от 10 020 руб. это будет 501 рубль ну и плюс основной Cashback это 1% за все покупки если траты у меня 50 100 рублей то это ещё плюс 501 рубль соответственно в целом за месяц получится 1002 рубля система очень сложная но я поняла поэтому благодарна Марии за такое подробное объяснение', 'negative', 'бонусные программы', NULL, 'Казань', 'Республика Татарстан', 'RU-TA', '2023-11-22 17:30:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('b0a9eb07-3dac-422b-a2c9-a6f7f6519830', 'banki.ru', 'Я очень редко прихожу в офис Банка, мобильное приложение разработано отлично, все действия я провожу там.', 'positive', 'мобильное приложение', NULL, 'Санкт-Петербург', 'Санкт-Петербург', 'RU-SPE', '2023-11-27 10:37:00+07', '2025-09-30 12:17:40.283265+07');
