INSERT INTO "public"."processed_reviews" VALUES ('5c0fd995-ab79-472e-9206-5035aa1afa8d', 'banki.ru', 'Так же ответил на все мои вопросы (их было много??) Алексей очень приветливый, грамотный и вежливый сотрудник. В дальнейшем планирую пользоваться услугами Газпромбанка, спасибо вам за вашу работу.', 'positive', 'общее', NULL, 'Ульяновск', 'Ульяновская область', 'RU-ULY', '2023-11-22 12:12:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('a75c401d-666d-4df7-a7cb-a0d0f6ad055b', 'banki.ru', 'Процесс получения Дебетовой карты очень меня Порадовал. Так как место доставки карты находилось за пределами зоны выдачи территориально. Но сотрудник Виктория приехала за пределы зоны своей ответственности и вручила карту, объяснив очень подробно Все кейсы предложений от Газпромбанка. Отношением к своим клиентам очень Доволен. Виктории Особенно Благодарен! ??????', 'positive', 'дебетовые карты', NULL, 'Пермь', 'Пермский край', 'RU-PER', '2023-11-21 12:43:00+07', '2025-09-30 12:17:40.283265+07');
