INSERT INTO "public"."processed_reviews" VALUES ('9d060c11-af67-4c68-9278-3108f5ef35b0', 'banki.ru', 'Встреча была назначена по улице Ставропольская, однако адрес мною был указан неверен в ходе заполнения заявки, в день встречи сотруднику банка Дмитрию сказала по телефону, что нахожусь на другом адресе на ул. уральская, что является другая часть города. Несмотря на такое непредвиденное обстоятельство, представитель приехал на мой адрес, поднялся на этаж. На встрече подсказал, как активировать карту. Узнала, что есть отличительные преимущества картой: бесплатное обслуживание, кэшбек на всё покупки от 5000р в мес. Встреча прошла около 15 минут и на отлично.', 'positive', 'бонусные программы', NULL, 'Краснодар', 'Краснодарский край', 'RU-KDA', '2023-11-18 15:37:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('72688e85-0cd3-4337-b574-f75d7f76bfc5', 'banki.ru', 'Быстрая и бесплатная доставка карты .Заказал через сайт дебетовую карту ,выбрал удобный день 14.11.2023 указал адрес доставки ,но к сожалению ,в день встречи оказался в другой районе города. Позвонил представитель банка Наталья ,для подтверждения встречи .Попросил её поменять адрес доставки, без проблем представитель доставила. Заранее позвонила о доставки .Все быстро и удобно ,помогла активировать карту ,где отключить смс ,рассказала про накопительный счет, оказался намного выгодней ,чем у меня открыт в другом банке ,вместе с ней и открыли его .Грамотный и компетентный специалист .', 'positive', 'дебетовые карты', NULL, 'Омск', 'Омская область', 'RU-OMS', '2023-11-21 18:37:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('1ca88fee-354b-4676-a271-a2b2457b756a', 'banki.ru', 'Представитель Газпромбанка, приехал что бы доставить дебетовую и кредитную карты. Молодой человек корректен, вежлив, обходителен, все доступно разъяснил , никаких сомнений по приобретению карт не возникло. Давно мечтала получить карту Газпромбанка, и вот наконец получилось . Качественная работа. Благодарю за работу!!!!', 'positive', 'дебетовые карты', NULL, 'Ульяновск', 'Ульяновская область', 'RU-ULY', '2023-11-20 11:58:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('fcc5c951-d4c9-4d41-b4d9-bc62e1014a4f', 'banki.ru', 'Обратилась в официальную группу в В*, где специалист ответил в 10:39 и подробно объяснил,как настроить автоплатеж по-шагово в мобильном приложении банка,чем очень помог. Решением моего вопроса Я осталась довольна, выражаю благодарность специалисту за помощь. Дополнение к отзыву отправлено Вам на электронную почту.', 'positive', 'мобильное приложение', NULL, 'Смоленск', 'Смоленская область', 'RU-SMO', '2023-11-22 11:53:00+07', '2025-09-30 12:17:40.283265+07');
