INSERT INTO "public"."processed_reviews" VALUES ('a583f03f-7e91-47fe-bf36-a111c46539b0', 'banki.ru', 'Накануне позвонил представитель банка Юрий, на следующий день назначил встречу на удобное мне время. За 15 минут позвонил, вручил мне карту, всё подробно объяснил. Осталось положительное впечатление. Всё прошло быстро, чётко, настоящий профессионал. Ставлю твердое 5!!!', 'positive', 'общее', NULL, 'Омск', 'Омская область', 'RU-OMS', '2023-11-22 08:24:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('06364921-015c-422d-ae19-a85d066992c8', 'banki.ru', 'Доставка и оформление карты прошли на раз два , быстро и качественно ,отдельное спасибо Карамяну Эдгарду , который дважды из-за меня менял место встречи , но без лишних вопросов и без капли недовольвства приехал , рассказал про кэшбэк , возможности карты и помог установить приложение , вообщем рекомендую.', 'positive', 'мобильное приложение', NULL, 'Омск', 'Омская область', 'RU-OMS', '2023-11-11 11:34:00+07', '2025-09-30 12:17:40.283265+07');
