INSERT INTO "public"."processed_reviews" VALUES ('ae769889-d964-4f75-8e51-52237add3e12', 'banki.ru', 'Хочу оценить сервис по доставке карты представителем банка. Ко мне приезжал Пол-ов Кирилл С.. Очень приятный и подкованный молодой человек. Заранее позвонил и согласовал удобное для меня время доставки. Подробно рассказал обо всех условиях по картам (дебетовая и кредитная),объяснил все нюансы работы с приложением, все сделал оперативно и быстро. я всем доволен!', 'positive', 'общее', NULL, 'Москва', 'Москва', 'RU-MOW', '2023-11-02 12:46:00+07', '2025-09-30 12:17:40.283265+07');
INSERT INTO "public"."processed_reviews" VALUES ('b228e05b-15a4-40ef-87ca-1f10d22fe2dc', 'banki.ru', '02.11.2023 заказал и получил карту мир от Газпромбанка для открытия накопительного счета, карту доставила представительница Ш-на Мария, на встречу приехала вовремя и выглядела опрятно, помогла зарегистрироваться в приложении и открыть подходящий под потребности накопительный счёт, приятно был удивлён, что мне выдали сим карту, для увеличения надбавки.', 'positive', 'дебетовые карты', NULL, 'Москва', 'Москва', 'RU-MOW', '2023-11-03 12:51:00+07', '2025-09-30 12:17:40.283265+07');
