INSERT INTO "public"."processed_reviews" VALUES ('8bc69e91-a865-4543-a06d-331a1a33e2dc', 'banki.ru', 'Столкнулся с невозможностью снятия денежных средств с банкомата 15 октября около 10утра. Оператор Газпромбанка проверил, объяснил лимиты на снятия с банкоматов Газпромбанка, оказалось что просто закончились денежные средства в банкомате, оператор посоветовал снять по лимиту с прочих банкоматов банков без процентов , и это выручило , спасибо', 'positive', 'общее', NULL, 'Махачкала', 'Республика Дагестан', 'RU-DA', '2023-10-19 10:16:00+07', '2025-09-30 12:17:40.283265+07');
