INSERT INTO "public"."processed_reviews" VALUES ('ea3a1984-da22-496b-a123-5df27ff2c0b1', 'banki.ru', 'Привезли быстро, договорились о удном мне времени , подождал курьер 15 мин. Всё быстро качественно , оформил сим-карту себе быстро, обрисовали быстро всё плюсы, качественный сервис . Буду думать на счёт карты Зенит голд Кредитной, условия осень хорошие, всём советую данный банк. Оценка данному банку 10 из 10', 'neutral', 'мобильная связь', NULL, 'Бийск', 'Алтайский край', 'RU-ALT', '2023-09-25 13:14:00+07', '2025-09-30 12:17:40.283265+07');
