INSERT INTO "public"."processed_reviews" VALUES ('d92a662a-cedf-4f13-abf2-92144698e267', 'banki.ru', 'Благодарю представителя банка Дмитрия, который пошел мне на встречу и приехал совсем в другую точку Москвы, чтобы доставить дебетовую карту, а также проконсультировать насчет потребительского кредита. Сложившийся форс-мажор никак не повлиял на качество услуг: Дмитрий очень профессионально проконсультировал, ответив на все возникающие вопросы, рассказал о других действующих продуктах банка, был очень доброжелателен.', 'positive', 'дебетовые карты', NULL, 'Москва', 'Москва', 'RU-MOW', '2023-08-23 20:07:00+07', '2025-09-30 12:17:40.283265+07');
