INSERT INTO "public"."processed_reviews" VALUES ('25ebd280-1dc7-4eb9-bfe9-f35fe97115f6', 'banki.ru', 'Хочется выразить благодарность представителю банка Ольге, которая объяснила все основные аспекты по интересующим меня продуктам банка 22 августа 2023 г. при выдаче карты. Не пользовался продуктами ГПБ раньше, но Ольна подробно объяснила и рассказала все услуги и их условия которые меня интересовали. Действительно очень компетентный сотрудник, спасибо, было приятно.', 'positive', 'общее', NULL, 'Рязань', 'Рязанская область', 'RU-RYA', '2023-08-22 17:11:00+07', '2025-09-30 12:17:40.283265+07');
