INSERT INTO "public"."processed_reviews" VALUES ('326f4063-5580-4376-8906-438d732e96ae', 'banki.ru', 'Добрый день. выражаю благодарность отделению банка Спб, метро Академическая Удобное местоположение отделения и часы работы. Приятный и комфортабельный офис. Вежливое обслуживание, сотрудник ответил на все мои вопросы, помог разобраться с приложением. Никаких очередей. Карту оформили и выдали сразу же. Спасибо.', 'positive', 'общее', NULL, 'Санкт-Петербург', 'Санкт-Петербург', 'RU-SPE', '2023-07-17 17:03:00+07', '2025-09-30 12:17:40.518303+07');
