INSERT INTO "public"."processed_reviews" VALUES ('0b6876a8-9fc9-4449-988e-7d81d8153aac', 'banki.ru', 'Менеджер заранее связался со мной и мы договорились о встрече. Встреча прошла быстро, подписали документы и выдали карту. Хочется поблагодарить представителя и Газпромбанк в частности. Обещали кэшбек 3% на все покупки и снятие наличных во всех банкоматах без комиссии.', 'negative', 'бонусные программы', NULL, 'Новосибирск', 'Новосибирская область', 'RU-NVS', '2023-07-14 13:50:00+07', '2025-09-30 12:17:40.518303+07');
