INSERT INTO "public"."processed_reviews" VALUES ('0856b281-d6a9-4475-8c95-23445057f809', 'banki.ru', 'Привезла карту сотрудник-Г-ва Фания Ф., в первый день встречи я просто забыл паспорт-сотрудник лояльно отнеслась к этому и просто привезла карту на другой день.', 'positive', 'общее', NULL, 'Ижевск', 'Удмуртская Республика', 'RU-UD', '2023-07-14 11:06:00+07', '2025-09-30 12:17:40.518303+07');
