INSERT INTO "public"."processed_reviews" VALUES ('82d2ca6d-16c7-4d99-986a-f666f49fe12e', 'banki.ru', 'Заказала дебетовую карту, привезли куда мне удобно (так получилось, что я поменяла место выдачи, отнеслись с пониманием) выдали быстро, дополнительных услуг не навязывали. Очень доброжелательный представитель. Съэкономила кучу времени, не нужно было идти в отделение и сидеть в долгих очередях! Хорошо организованный сервис! Спасибо большое!', 'positive', 'дебетовые карты', NULL, 'Барнаул', 'Алтайский край', 'RU-ALT', '2023-07-26 06:31:00+07', '2025-09-30 12:17:40.518303+07');
