INSERT INTO "public"."processed_reviews" VALUES ('6839c59a-02ec-4557-b3ab-4a578fbd8938', 'banki.ru', 'Газпромбанк постоянно совершенствует сервис в работе с клиентами. Предложения банка находятся в личном кабинете приложения, что очень удобно. Так же пользуюсь постоянно приложением Город Пэй, что исключает невозможность оплаты в городском транспорте. При необходимости обращения в офис тратится немного времени, ожидание комфортное.', 'negative', 'общее', NULL, 'Калининград', 'Калининградская область', 'RU-KGD', '2023-07-07 18:23:00+07', '2025-09-30 12:17:40.518303+07');
