INSERT INTO "public"."processed_reviews" VALUES ('d026e140-1019-402c-b20d-6842a65c77be', 'banki.ru', 'Менеджер Алексей быстро связался со мной и через пару часов карта была у меня. Причем, как я поняла, он из Новосибирска, а я вообще была на даче за Бердском. Алексей подробно меня проконсультировал, как установить приложение, какие настройки добавить/отключить, если нужно.', 'positive', 'мобильное приложение', NULL, 'Новосибирск', 'Новосибирская область', 'RU-NVS', '2023-07-19 12:18:00+07', '2025-09-30 12:17:40.518303+07');
