INSERT INTO "public"."processed_reviews" VALUES ('2dd505c6-c1ee-4fc1-ba19-5552d5289447', 'banki.ru', 'Я являюсь держателем дебетовой карты Газпромбанка.При оформлении карты никакие услуги информирования по операциям по карте подключены не были ,так как я на этом настаивала и мне все отключили.26 июня произошло списание 99руб.,за смс информирование по карте.', 'positive', 'дебетовые карты', NULL, 'Волжский', 'Волгоградская область', 'RU-VGG', '2023-06-28 08:48:00+07', '2025-09-30 12:17:40.518303+07');
INSERT INTO "public"."processed_reviews" VALUES ('8717eaf5-11c3-45ab-be2e-6322cdbd3429', 'banki.ru', 'Хочу поблагодарить представителя газпромбанка Марию,за качественный сервис. Мария приехала во время, рассказала подробно и доступным языком про условия. Доставка была к город Можга 23.06.2023. Помогла активировать карту и присвоить пинкод, помогла выбрать выгодную категорию кэшбека. Спасибо газпромбанку и Марии !)', 'positive', 'общее', NULL, 'Можга', 'Удмуртская Республика', 'RU-UD', '2023-06-23 11:51:00+07', '2025-09-30 12:17:40.518303+07');
