INSERT INTO "public"."processed_reviews" VALUES ('b2cb33c4-368d-4c7c-93c8-59a1f288f7f9', 'banki.ru', 'Заказала карту газпрома, узнав о хороших банковских условиях. После оформления карты, позвонили, все рассказали, уточнили информацию. После, позвонила К-ева Владислава Р., подстроилась под мое плотное расписание и привезла в точное время, о котором договорились. Сразу предупредила сколько нужно времени для оформления документов (удобно планировать свое время). Доброжелательная девушка, все объяснила, как пользоваться. Спасибо, за таких сотрудников', 'neutral', 'дебетовые карты', NULL, 'Воронеж', 'Воронежская область', 'RU-VOR', '2023-06-15 12:06:00+07', '2025-09-30 12:17:40.518303+07');
INSERT INTO "public"."processed_reviews" VALUES ('6d32d792-42e9-4f45-84af-4eb43e7e5abb', 'banki.ru', 'Положительный опыт общения с представителем банка Евгенией. Приехала ко мне в офис, в оговоренное время. Быстро и профессионально оформила необходимые документы. Рассказала об условиях использования дебетовой карты, услугах и сервисах, программах лояльности.', 'positive', 'дебетовые карты', NULL, 'Липецк', 'Липецкая область', 'RU-LIP', '2023-06-01 16:41:00+07', '2025-09-30 12:17:40.518303+07');
