INSERT INTO "public"."processed_reviews" VALUES ('c0f09b60-26f8-41ca-9413-e4b2e28bb5d3', 'banki.ru', 'Пришла в офис ГПБ решить вопрос о замене дебетовой карты по причине ее размагничивания. В очереди пробыла не больше 5-7 минут. Операционист очень вежливая девушка быстро решила мой вопрос, профессионально и грамотно ответила на все возникающие у меня вопросы. Помимо этого были решены еще два моих дополнительных запроса, которые заняли дополнительное время уже после окончания рабочего времени сотрудников. Спасибо им за терпение) Банк советую всем!', 'negative', 'дебетовые карты', NULL, 'Надым', 'Ямало-Ненецкий автономный округ', 'RU-YAN', '2023-04-19 04:31:00+07', '2025-09-30 12:17:40.518303+07');
