INSERT INTO "public"."processed_reviews" VALUES ('033e7f06-2a26-4ba7-b521-21130ad5e6f4', 'banki.ru', 'В банке нравится все. И обслуживание на отлично, и дистанционное. Кэшбэк система, сам поступает на счет. Кредитная карта на 180 дней. Если выбирать то данный банк однозначно. Банк заинтересован в клиентах, не как другие банки - ""сам же к нам деньги принес, мы тебя не звали"". В данном банке ты клиент. Всегда все информативно.', 'positive', 'кредитные карты', NULL, 'Новосибирск', 'Новосибирская область', 'RU-NVS', '2023-03-10 15:04:00+07', '2025-09-30 12:17:40.518303+07');
