INSERT INTO "public"."processed_reviews" VALUES ('e8cd7eaa-512d-4714-a95b-9c2fe0ac74e3', 'banki.ru', 'В ноябре 2022 года меняла карту в Газпромбанке, при смене карты мне подключили не нужную мне услугу ГПБ плюс, эта услуга первый месяц бесплатная, потом 199 руб. Будьте осторожны. Банк разочаровал.. Жаль что это зарплатная карта, так бы ушла из этого банка, так вы теряете своих клиентов, обманом снимаете деньги за услуги, в которых я не нуждаюсь.', 'negative', 'зарплатные карты', NULL, 'Дзержинск', 'Нижегородская область', 'RU-NIZ', '2023-03-01 12:55:00+07', '2025-09-30 12:17:40.518303+07');
